mapper.select:
SELECT articleno, title, regdate, hit, id, attachfilenum, commentnum FROM ( SELECT rownum r, a.* FROM ( SELECT a.articleno, title, a.regdate, hit, id, count(distinct(f.attachfileno)) attachfilenum, count(distinct(c.commentno)) commentnum FROM article a, attachfile f, comments c WHERE a.articleno = f.articleno(+) AND a.articleno = c.articleno(+) AND boardcd = #{boardCd}
AND (title LIKE '%${searchWord}%' OR content LIKE '%${searchWord}%')
GROUP BY a.articleno, title, a.regdate, id, hit ORDER BY articleno DESC ) a ) WHERE r BETWEEN #{start} AND #{end}

SELECT count(*) FROM article WHERE boardcd = #{boardCd}


   AND (title LIKE '%${searchWord}%' OR
   content LIKE '%${searchWord}%')

mapper.update:
UPDATE article
SET title=#{title},content=#{content}
WHERE articleno=#{articleNo}

mapper.select:
SELECT articleno, title, content, hit, id, regdate
FROM article
WHERE articleno = #{articleNo}

SELECT commentno,articleno, memo,regdate 
FROM comments 
WHERE commentno = #{commentNo}
