mapper.update:
update APART_INFO_DOC
set
INFO_DOC_SUB = #{INFO_DOC_SUB},
INFO_DOC_FILE = #{INFO_DOC_FILE , jdbcType = VARCHAR},
INFO_DOC_ORIGINAL = #{INFO_DOC_ORIGINAL, jdbcType = VARCHAR}
where INFO_DOC_NUM = #{INFO_DOC_NUM}

mapper.select:
select * from (select rownum rnum, INFO_DOC_NUM, Admin_no, INFO_DOC_SUB, INFO_DOC_DATE,INFO_DOC_FILE, INFO_DOC_ORIGINAL from (select rownum rnum,INFO_DOC_NUM,Admin_no,INFO_DOC_SUB,INFO_DOC_DATE,INFO_DOC_FILE,INFO_DOC_ORIGINAL from APART_INFO_DOC
INFO_DOC_SUB like #{find_name}
order by INFO_DOC_DATE desc) ) where rnum >= ((#{page} - 1) * 5 + 1) and rnum <= (#{page} * 5)
